// Fonction helper pour classifier le repo
function classifyRepo(repo) {
  const lang = (repo.language || '').toLowerCase();
  const topics = (repo.topics || []).map(t => t.toLowerCase());
  const desc = (repo.description || '').toLowerCase();
  const name = repo.name.toLowerCase();
  const all = [lang, ...topics, desc, name].join(' ');

  if (all.match(/react|vue|angular|frontend|ui|css|html|tailwind|next/)) return 'frontend';
  if (all.match(/node|express|api|backend|server|django|flask|fastapi/)) return 'backend';
  if (all.match(/docker|kubernetes|ci|cd|devops|terraform|ansible/)) return 'devops';
  if (all.match(/ai|ml|data|tensorflow|pytorch|llm|rag|embedding|streamlit/)) return 'ai_data';
  if (all.match(/mobile|ios|android|react-native|flutter/)) return 'mobile';
  if (all.match(/database|sql|postgres|mongo|mysql/)) return 'database';
  if (all.match(/automation|n8n|workflow|script/)) return 'automation';

  return 'other';
}

