  tools: ['git', 'vscode', 'n8n', 'postman', 'figma', 'webpack', 'vite', 'babel']
};

function extractKeywords(text) {
  const lowerText = text.toLowerCase();
  const found = new Set();

  // Chercher tous les keywords
  Object.values(TECH_KEYWORDS).flat().forEach(keyword => {
    if (lowerText.includes(keyword)) {
      found.add(keyword);
    }
  });

  return Array.from(found);
}

function classifyImportance(type, contentLength, keywords) {
  // Importance critique
  if (type === 'portfolio_overview' || type === 'skills_comprehensive') {
    return 'critical';
  }

  // Importance haute
  if (type === 'readme_full' || 
      type === 'repository_overview' || 
      type?.startsWith('qa_') ||
      type === 'technical_details') {
    return 'high';
  }

  // Bas√© sur le contenu
  if (keywords.length > 5) return 'high';
  if (contentLength > 500) return 'medium';

  return 'low';
}

function detectCategory(text, existingCategory) {
  if (existingCategory && existingCategory !== 'other') {
    return existingCategory;
  }

  const lower = text.toLowerCase();

  if (lower.match(/react|vue|angular|frontend|ui|css|html|tailwind/)) return 'frontend';
  if (lower.match(/node|express|api|backend|server|django|flask|fastapi/)) return 'backend';
  if (lower.match(/docker|kubernetes|ci|cd|devops|terraform|ansible/)) return 'devops';
  if (lower.match(/ai|ml|data|tensorflow|pytorch|llm|rag|embedding/)) return 'ai_data';
  if (lower.match(/database|sql|postgres|mongo|mysql|redis/)) return 'database';
  if (lower.match(/mobile|ios|android|react-native|flutter/)) return 'mobile';

  return 'other';
}

