  const meta = item.json.metadata || {};
  const content = item.json.pageContent;

  // Extraire keywords
  const keywords = extractKeywords(content);

  // Classifier importance
  const importance = classifyImportance(meta.type, content.length, keywords);

  // D√©tecter cat√©gorie si manquante
  const category = detectCategory(content, meta.category);

  // Score de pertinence (pour priorisation)
  let relevanceScore = 0;
  if (importance === 'critical') relevanceScore += 100;
  else if (importance === 'high') relevanceScore += 50;
  else if (importance === 'medium') relevanceScore += 25;
  relevanceScore += keywords.length * 5;
  if (meta.type?.startsWith('qa_')) relevanceScore += 30;

  enhanced.push({
