const items = $input.all();
const chunkedDocs = [];

function smartChunk(rawText, maxLength = 800, overlap = 150) {
  const text = String(rawText || "");
  if (text.length <= maxLength) return [text];

  const chunks = [];
  const paragraphs = text.split(/\n\n+/);

  if (paragraphs.length > 1) {
    let currentChunk = "";
    let previousChunk = "";

    for (const para of paragraphs) {
      const testChunk = currentChunk + (currentChunk ? "\n\n" : "") + para;

      if (testChunk.length <= maxLength) {
        currentChunk = testChunk;
      } else {
        if (currentChunk.length > 100) {
          chunks.push(currentChunk.trim());
          previousChunk = currentChunk;
        }

        if (previousChunk && overlap > 0) {
          const words = previousChunk.split(" ");
          const overlapWords = words.slice(-Math.floor(overlap / 6));
          currentChunk = overlapWords.join(" ") + "\n\n" + para;
        } else {
          currentChunk = para;
        }
      }
    }

    if (currentChunk.length > 100) chunks.push(currentChunk.trim());
  } else {
    const sentences = text.match(/[^.!?\n]+[.!?\n]+/g) || [text];
    let currentChunk = "";
    let previousChunk = "";

    for (const sentence of sentences) {
      const trimmedSentence = sentence.trim();

      if ((currentChunk + " " + trimmedSentence).length <= maxLength) {
        currentChunk += (currentChunk ? " " : "") + trimmedSentence;
      } else {
        if (currentChunk.length > 100) {
          chunks.push(currentChunk.trim());
          previousChunk = currentChunk;
        }

        if (previousChunk && overlap > 0) {
          const words = previousChunk.split(" ");
          const overlapWords = words.slice(-Math.floor(overlap / 6));
          currentChunk = overlapWords.join(" ") + " " + trimmedSentence;
        } else {
          currentChunk = trimmedSentence;
        }
      }
    }

    if (currentChunk.length > 100) chunks.push(currentChunk.trim());
  }

  return chunks.filter((c) => c.length > 50);
}

for (const [i, item] of items.entries()) {
  try {
    const dataArray = item.json?.data ?? [];
    if (!Array.isArray(dataArray)) continue;

    for (const [j, sub] of dataArray.entries()) {
      const content = sub.pageContent ?? "";
      const metadata = sub.metadata ?? {};

      if (!content || typeof content !== "string") continue;

      // Pas de d√©coupe si texte court ou important
      if (
        content.length <= 800 ||
        metadata.importance === "critical" ||
        metadata.type?.startsWith("qa_")
      ) {
        chunkedDocs.push({
          json: {
            pageContent: content,
            metadata: { ...metadata },
          },
        });
        continue;
      }

      const chunks = smartChunk(content, 800, 150);

      if (chunks.length === 1) {
        chunkedDocs.push({
          json: {
            pageContent: content,
            metadata: { ...metadata },
          },
        });
      } else {
        chunks.forEach((chunk, index) => {
          chunkedDocs.push({
            json: {
              pageContent: chunk,
              metadata: {
                ...metadata,
                is_chunked: true,
                chunk_index: index + 1,
                total_chunks: chunks.length,
                original_length: content.length,
                chunk_strategy:
                  chunks.length > 3 ? "paragraph" : "sentence",
              },
            },
          });
        });
      }
    }
  } catch (err) {
    console.error(`‚ùå Erreur sur item ${i}:`, err.message);
    continue;
  }
}

return chunkedDocs;

